                check("desktop");
            });
            $("input[name=desktopDiffer_bg]").keyup(function() {
                bg("desktop");
            });
            $("input[name=desktopDiffer_rad]").change(function() {
                radius("desktop");
            });
            $("input[name=desktopDiffer_size]").change(function() {
                size("desktop");
            });

            // iOS
            $("input[name=iosType]").click(function() {
                if ($(this).prop("id") == "iosDiffer") {
                    $(".iosDifferDiv").show();
                } else {
                    $(".iosDifferDiv").hide();
