                size("android");
            });

            // Windows
            $("input[name=winType]").click(function() {
                if ($(this).prop("id") == "winWhite") {
                    $(".winIcon img").css("-webkit-filter", "brightness(0) invert(1)")
                                     .css("filter", "brightness(0) invert(1)");
                } else {
                    $(".winIcon img").css("-webkit-filter", "none")
                                     .css("filter", "none");
                }
            });
